// slick 
$(document).ready(function () {
	$('.slider').slick({
		autoplay: true,
		infinite: true,
		speed: 500,
		fade: true,
		cssEase: 'linear',
		arrows: false,
		centerMode: true
	});
});

// Header Scroll
$(window).on('scroll', function () {
	var scroll = $(window).scrollTop();

	if (scroll >= 50) {
		$('.navbar').addClass('fixed');
	} else {
		$('.navbar').removeClass('fixed');
	}
});

// scroll
$('.menu-list a, .btn').on('click', function (event) {
	if (this.hash !== '') {
		event.preventDefault();
		const hash = this.hash;
		$('html, body').animate({
			scrollTop: $(hash).offset().top - 0
		}, 800);
	}
});

// popup
$('.works-blok').magnificPopup({
	delegate: 'a',
	type: 'image'
});

// Mobile Navigation
$('.mobBtn').on('click', function () {
	$(this).toggleClass('rotate');
	$('.menu-list').toggleClass('open');
	return false;
});
$(' .menu-list').find('a').on('click', function () {
	if ($('.mobBtn').css('display') == 'flex') {
		$('.mobBtn').toggleClass('rotate');
		$('.menu-list').toggleClass('open');
	}

});