$(document).ready(function () {

	// Header Scroll
	$(window).on('scroll', function () {
		var scroll = $(window).scrollTop();

		if (scroll >= 50) {
			$('.navbar').addClass('fixed');
		} else {
			$('.navbar').removeClass('fixed');
		}
	});

	// accordion
	$(".accordion-content").hide();
	$(".accordion").on("click", ".accordion-header", function () {
		$(this).toggleClass("active").next().slideToggle(400);
	});

	// price
	$(".card").hide();
	$('#procedure').on('click', function (e) {
		$('.card').toggleClass('hide').slideToggle(400);
		e.preventDefault();
	});

	$(".card-correct").hide();
	$('#correct').on('click', function (e) {
		$('.card-correct').toggleClass('hide').slideToggle(400);
		e.preventDefault();
	});


	// scroll
	$('.menu-link, .contact-link').on('click', function (event) {
		if (this.hash !== '') {
			event.preventDefault();
			const hash = this.hash;
			$('html, body').animate({
				scrollTop: $(hash).offset().top - 200
			}, 800);
		}
	});

	// Mobile Navigation
	$('.mobBtn').on('click', function () {
		$(this).toggleClass('rotate');
		$('.menu-list').toggleClass('open');
		return false;
	});
	$(' .menu-list').find('a').on('click', function () {
		if ($('.mobBtn').css('display') == 'flex') {
			$('.mobBtn').toggleClass('rotate');
			$('.menu-list').toggleClass('open');
		}

	});
});